<?php/** * The template for displaying the Folios page * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#single-post * * @package Ayin * @since 1.0.0 */get_header();?><section id="primary" class="content-area">	<main id="main" class="site-main" role="main"><?php		// define these two variables here so we can access them in JavaScript at bottom of file		$defaultImage = '';		$defaultCaption = '';		while ( have_posts() ) : ?>		<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>			<div class="entry-content"><?php				wp_reset_postdata();				the_post();				the_content(					sprintf(						wp_kses(						/* translators: %s: Name of current post. Only visible to screen readers */							__( 'Continue reading<span class="screen-reader-text"> "%s"</span>', 'ayin' ),							array(								'span' => array(									'class' => array(),								),							)						),						get_the_title()					)				);				echo(					'<div class="columns-page-container alignwide">						<div class="columns-grid">'				);				$all_categories = get_categories( array(					'parent' => '0' //get top level categories only				));				// look for the category id for the "Folios" category only				$foliosCatId = 0;				$child_cats = null;				foreach( $all_categories as $single_category ){					if ($single_category->slug == 'folio') {						$foliosCatId = $single_category->cat_ID;					}				}				// get children of "Folio" category only				if ($foliosCatId > 0) {					$child_cats = get_categories( array(						'child_of' => $foliosCatId						//category_display_order					));				}				// display list of columns categories and the posts associated with each				if ($child_cats) {					// first, get them in the right order (reverse display order, so highest number first					$sorted_cats = array();					foreach($child_cats as $cat){						$sorted_cats[] = array(							'd' => get_field('category_date_field', 'category_' . $cat->term_id),							'cat' => $cat						);					}					// sort by date using custom function at bottom of this file					usort($sorted_cats, 'date_compare');					// second, display each column					$firstItem = true;										echo '	<div class="ColumnChildren">';					foreach( $sorted_cats as $child_cat ){						$childID = $child_cat['cat']->cat_ID;						if (!empty($child_cat['d'])) {							$folioDate = DateTime::createFromFormat('m/d/Y', $child_cat['d']);						} else {							$folioDate = null;						}						// get list of all posts within this category and set column category settings						$folioName = $child_cat['cat']->name;						$folioDescription = $child_cat['cat']->description;						$folioLink = get_term_link( $child_cat['cat'] );						$sorted_posts = array();						$posts = get_posts( array(							'cat' => $childID,							'posts_per_page' => 3						));						if (sizeof($posts) > 0) {							foreach ($posts as $post) {								$sorted_posts[] = array(									'display_order' => get_field('folio_order', $post->ID),									'title' => $post->post_title,									'post' => $post,								);							}							// sort by display_order and then title							usort($sorted_posts, 'post_compare');						}						echo '			<h2 class="FolioTitle"><a href="' . $folioLink . '">' . esc_attr($folioName) . '</a></h2>';						if ($folioDate) echo ' <span class="FolioDate Parent" style="white-space: nowrap;"> &mdash; ' . $folioDate->format('F j, Y') . '</span>';						echo '			<p class="FolioParentDescription">' . $folioDescription . '<p>';						echo '			<div class="ColumnChild">';						foreach ($sorted_posts as $item) {							$folioTitle = get_the_title($item['post']->ID);							$folioPermalink = get_permalink($item['post']->ID);							$folioThumbnail = get_the_post_thumbnail_url($item['post']->ID, 'single-post-thumbnail');							$folioExcerpt = get_the_excerpt($item['post']->ID);							$folioAuthor = get_field('column_author_name', $post->ID);							$postCat = get_the_category($post->ID);							if ($postCat) {								if ($postCat[0]->category_parent == 0) {									$catName = $postCat[0]->name;									$catSlug = $postCat[0]->slug;								} else {									$parentCat = get_category($postCat[0]->category_parent);									$catName = $parentCat->name;									$catSlug = $parentCat->slug;								}							} else {								$catName = '';								$catSlug = '';							}							if (has_post_thumbnail()) {								$thumbnail = wp_get_attachment_image_src(get_post_thumbnail_id($item['post']->ID), 'full');								$thumbnail_url = $thumbnail[0];								echo('		<article class="ColumnArticle" data-thumbnail="' . esc_attr($folioThumbnail) . '" data-title="' . esc_attr($folioTitle) . '">');								echo('			<a href="' . $folioPermalink . '" class="GridThumb" style="background-image:url(' . $thumbnail_url . '"></a>');								echo('			<div class="entry-wrapper">');								if (!empty($catName) && !empty($catSlug)) {									echo('				<div class="entry-meta">');									echo('					<div class="cat-links"><a href="https://ayinpress.org/category/' . $catSlug . '/">' . esc_html($catName) . '</a></div>');									echo('				</div>');								}								echo('				<h2 class="ArticleTitle entry-title"><a href="' . $folioPermalink . '">' . esc_html($folioTitle) . '</a></h2>');								echo('				<div class="FolioExcerpt"><p>' . $folioExcerpt . '</p></div>');								if (!empty ($folioAuthor)) {									echo('			<div class="entry-meta">');									echo('				<span class="byline">');									echo('					<span class="author-prefix">by</span> <span class="author vcard">' . $folioAuthor . '</span>');									echo(' 				</span>');									echo('			</div>');								}								echo('			</div>');								echo('		</article>');							}						}						echo('		</div>');						echo('		<a class="PostGrid Folios" href="' . $folioLink . '" data-title="' . esc_attr($folioName) . '">Read the Full Folio</a>');						echo('		<div class="PostGridSpacer"></div>');						wp_reset_postdata();					}					echo('	</div>');					echo(					'	</div>					</div>					');				}				endwhile; // End of the loop. ?>			</div>		</article><!-- #post-<?php the_ID(); ?> -->	</main><!-- #main --></section><!-- #primary --><?phpget_footer();// compare by datefunction date_compare($element1, $element2) {	$datetime1 = strtotime($element1['d']);	$datetime2 = strtotime($element2['d']);	return $datetime2 - $datetime1;}// compare by display order first; if those are equal, then display by title alphabeticallyfunction post_compare($element1, $element2) {	$displayOrder1 = $element1['display_order'];	$displayOrder2 = $element2['display_order'];	if (empty($displayOrder1)) {		$displayOrder1 = 100;	} else {		$displayOrder1 = intval($displayOrder1);	}	if (empty($displayOrder2)) {		$displayOrder2 = 100;	} else {		$displayOrder2 = intval($displayOrder2);	}	if ($displayOrder1 == $displayOrder2) {		return strcmp($element1['title'], $element2['title']);	} else {		return $displayOrder1 - $displayOrder2;	}}